<?phpif ( ! defined( 'ABSPATH' ) ) {	exit; // Exit if accessed directly.}if ( ! osf_is_elementor_activated() ) {	return;}use Elementor\Controls_Manager;class OSF_Vertical_Line {	public function __construct() {		add_filter( 'osf_theme_custom_inline_css', array( $this, 'render_css' ) );		add_action( 'elementor/element/section/section_layout/after_section_end', [			$this,			'register_controls'		], 10, 2 );		add_filter('body_class', [$this, 'check_vertical_line']);	}	public function check_vertical_line($classes){		if(!get_theme_mod('osf_vertical_line_switch', true)){			$classes[] = 'opal-diable-vertical-line';		}		return $classes;	}	public function render_css( $css ) {		if ( ! empty( get_theme_mod( 'osf_vertical_line_color' ) ) && get_theme_mod( 'osf_vertical_line_color' ) !== '#f2f2f2' ) {			$css .= '.vertical-lines .line-item { background-color:' . get_theme_mod( 'osf_vertical_line_color' ) . ';} .vertical-lines .line-wrap{ border-color: ' . get_theme_mod( 'osf_vertical_line_color' ) . ';}';		}		return $css;	}	public function register_controls( $element, $args ) {		$element->start_controls_section(			'osf_section_vertical_line',			[				'label' => __( 'Vertical Line', 'rehomes-core' ),				'tab'   => Controls_Manager::TAB_LAYOUT,			]		);		$element->add_control( 'osf_section_vertical_line_switcher',			[				'label'              => __( 'Enable Vertical Line', 'rehomes-core' ),				'type'               => Controls_Manager::SWITCHER,				'default'            => '',				'frontend_available' => true,				'label_on'           => 'Yes',				'label_off'          => 'No',				'return_value'       => 'active',				'prefix_class'       => 'osf-vertical-line-'			]		);		$element->add_control( 'osf_section_vertical_line_color',			[				'label'     => __( 'Color', 'rehomes-core' ),				'type'      => Controls_Manager::COLOR,				'selectors' => [					'{{WRAPPER}} .vertical-lines .line-wrap' => 'border-color: {{VALUE}};',					'{{WRAPPER}} .vertical-lines .line-item' => 'background-color: {{VALUE}};',				],				'condition' => [					'osf_section_vertical_line_switcher' => 'active'				]			]		);		$element->end_controls_section();	}}new OSF_Vertical_Line();